sqlMap.select:
WITH LIST AS ( SELECT ROW_NUMBER() OVER (ORDER BY $sortColumn$ $sortDirection$) AS ROWNUMBER, A.ISBN, A.thumbnail, F.NAME AS BOOKCATEGORYROOT, E.NAME AS BOOKCATEGORYSUB, D.NAME AS BOOKCATEGORY, A.TITLE, A.AUTHORS, ISNULL(A.COUNT,0) AS COUNT, A.PUBLISHEDDATE, A.RegDate, COUNT(B.ISBN) AS RENTALCOUNT, ISNULL(C.RESERVATIONCOUNT,0) AS RESERVATIONCOUNT FROM BookInfo A LEFT OUTER JOIN BookRental B ON A.ISBN = B.ISBN AND B.RETURNDATE IS NULL LEFT OUTER JOIN ( select isbn , count(*) as RESERVATIONCOUNT from bookreservation where ISCANCELED = 0 AND BOOKRENTAL_ID IS NULL group by isbn ) C ON A.ISBN = C.ISBN INNER JOIN BOOKCATEGORY D ON A.CATEGORY = D.ID INNER JOIN BOOKCATEGORYSUB E ON D.SUB_ID = E.ID INNER JOIN BOOKCATEGORYROOT F ON E.ROOT_ID = F.ID
GROUP BY A.ISBN,a.thumbnail, A.TITLE,D.NAME, E.NAME, F.NAME, A.AUTHORS, A.COUNT, A.PUBLISHEDDATE, A.RegDate, C.RESERVATIONCOUNT
) SELECT * FROM LIST WHERE ROWNUMBER BETWEEN #startRow# AND #endRow#

SELECT COUNT(A.TITLE) FROM ( SELECT ISNULL(COUNT(DISTINCT(A.TITLE)),0) AS TITLE FROM BookInfo A LEFT OUTER JOIN BookRental B ON A.ISBN = B.ISBN AND B.RETURNDATE IS NULL LEFT OUTER JOIN ( select isbn , count(*) as RESERVATIONCOUNT from bookreservation where ISCANCELED = 0 AND BOOKRENTAL_ID IS NULL group by isbn ) C ON A.ISBN = C.ISBN INNER JOIN BOOKCATEGORY D ON A.CATEGORY = D.ID INNER JOIN BOOKCATEGORYSUB E ON D.SUB_ID = E.ID INNER JOIN BOOKCATEGORYROOT F ON E.ROOT_ID = F.ID
GROUP BY A.ISBN,a.thumbnail, A.TITLE, D.NAME, E.NAME, F.NAME, A.AUTHORS, A.COUNT, A.PUBLISHEDDATE, C.RESERVATIONCOUNT
) A
